/* eslint-disable import/prefer-default-export */
import axios from 'axios';
import { FETCH_RENTALS } from '../types';

const fetchRentalsAction = rentals => ({
  type: FETCH_RENTALS,
  rentals,
});

export const fetchRentals = () => async (dispatch) => {
  await axios
    .get('http://127.0.0.1:3001/rentals', {
      headers: { 'x-access-token': sessionStorage.getItem('jwt_token') },
    })
    .then(rentals => dispatch(fetchRentalsAction(rentals)))
  // eslint-disable-next-line no-console
    .catch(err => console.error(err));
};
