import store from 'store';
import { FETCH_RENTALS, UPDATE_CURRENT_RENTAL } from '../types';


const initialState = {
  rentals: [],
  currentRental: store.get('currentRental') || {},
};

export default (state = initialState, action) => {
  switch (action.type) {
    case FETCH_RENTALS:
      return {
        ...state,
        rentals: action.rentals,
      };
    case UPDATE_CURRENT_RENTAL:
      return {
        ...state,
        currentRental: store.get('currentRental'),
      };
    default:
      return state;
  }
};
