/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import React, { Component } from 'react';
import _ from 'lodash';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Dropdown, DropdownToggle, DropdownMenu } from 'reactstrap';
import { Link } from 'react-router-dom';
import { fetchRentals } from '../store/actions/rentalsActions';
import '../styles/Nav.css';

class NavbarDropdown extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listOpen: false,
    };
  }

  componentDidMount() {
    const { fetchRentals } = this.props;
    fetchRentals();
  }

  componentDidUpdate(prevProps) {
    const { rentals: { rentals }, isLoggedIn, fetchRentals } = this.props;
    // eslint-disable-next-line no-multi-spaces
    const propsComparisonResult =  _.isEqual(rentals, prevProps.rentals);
    if (!propsComparisonResult || prevProps.isLoggedIn !== isLoggedIn) {
      fetchRentals();
    }
    return null;
  }

  handleClickOutside = () => {
    this.setState({ listOpen: false });
  }

  toggleList = () => {
    const { listOpen } = this.state;
    this.setState({ listOpen: !listOpen });
  }

  selectItem = (rental) => {
    const { updateCurrentRental, toggleNav } = this.props;
    this.toggleList();
    toggleNav();
    updateCurrentRental(rental);
  }

  render() {
    const { rentals: { rentals } } = this.props;
    const { listOpen } = this.state;
    return (
      <Dropdown nav inNavbar isOpen={listOpen} toggle={this.toggleList}>
        <DropdownToggle nav caret>
          { rentals.length > 0 ? 'Select Rental' : 'No Properties' }
        </DropdownToggle>
        <DropdownMenu style={{ background: '#989b9e' }}>
          {
            listOpen && (
              rentals.map((item) => {
                const { _id: id } = item;
                return (
                  <Link to={`/rentals/${id}`} id="dropdown-item" className="dropdown-item text-center" key={id} onClick={() => this.selectItem(item)}>
                    {item.address1}
                  </Link>
                );
              })
            )}
        </DropdownMenu>
      </Dropdown>
    );
  }
}

NavbarDropdown.propTypes = {
  rentals: PropTypes.arrayOf(
    PropTypes.objectOf(
      PropTypes.oneOfType([
        PropTypes.string,
        PropTypes.number.isRequired,
      ]),
    ).isRequired,
  ).isRequired,
  updateCurrentRental: PropTypes.func.isRequired,
  toggleNav: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  rentals: state.rentals,
});

export default connect(mapStateToProps, { fetchRentals })(NavbarDropdown);
