/* eslint-disable import/prefer-default-export */
import axios from 'axios';
import { FETCH_RENTALS } from '../types';

const fetchRentalsAction = rentals => ({
  type: FETCH_RENTALS,
  rentals,
});

const getData = url => axios
  .get(url, {
    headers: { 'x-access-token': sessionStorage.getItem('jwt_token') },
  })
  .then(res => res.json())
  .then(data => data);

export const fetchRentals = () => async (dispatch) => {
  console.log('Dispatch');
  const response = await getData('http://127.0.0.1:3001/rentals');
  dispatch(fetchRentalsAction(response));
};
